import type * as Stitches from '../index.js'
import { createStitches } from '../index.js'

const { css } = createStitches({})

const expressor = css({
	color: 'CurrentColor',
	variants: {
		color: {
			blue: {
				color: 'Blue',
			},
			pink: {
				color: 'Pink',
			},
		}
	}
})

const props: Stitches.VariantProps<typeof expressor> = {
	color: 'blue'
}

type textVariants = Stitches.VariantProps<typeof text>

const text = css({
	color: 'red',
	variants: {
		mySize: {
			myLarge: {},
		},
	},
})

const vars: textVariants = {
	mySize: {
		"@all": 'myLarge'
	},
}

void vars
